'        trailWidth: 1,',
'        svgStyle: null',
'        });',
'var submitButtonID;',
'',
'function insertAfter(referenceNode, newNode) {',
'  referenceNode.insertBefore(newNode, referenceNode.nextSibling);',
'}',
'',
'function getFilename(){',
'    if ($(''#P2_FILE'')[0].files.length) {',
'        return $(''#P2_FILE'')[0].files[0].name;',
'    }',
'    return undefined;',
'			',
'}',
'',
'function sleep(delay) {',
'    var start = new Date().getTime();',
'    while (new Date().getTime() < start + delay);',
'}',
'',
'async function process(parser) {',
'        ',
'        ',
'        var promiseArray = [];',
'        if ( parser )',
'            parser.pause();',
'        ',
'        for (j = 0; j < q.length(); j++) {',
'            //var parsedData =  q.dequeue();',
'            //var parsedDataStr = JSON.stringify(parsedData);',
'            ajaxPromise =  apex.server.process(',
'                                "ping", ',
'                                //{ p_clob_01: parsedDataStr},',
'                                { p_clob_01: JSON.stringify(q.dequeue())},',
'                                {dataType:"text"}',
'                            );                            ',
'            promiseArray.push(ajaxPromise);         ',
'            ',
'        }',
'',
'        try {',
'            ',
'            const resultArray = await Promise.all(promiseArray);',
'            //if ( parser )',
'            //    parser.resume();',
'            for (let i = 0; i<resultArray.length; i++){',
'                 ajaxCallCounter++;',
'                 console.log(resultArray[i] + '' totalChunks: ''+ totalChunks + '' chunked:'' + chunked + '' totalAjax: '' + ajaxCallCounter); ',
'                 bar.animate(ajaxCallCounter/totalChunks);',
'                 processedSize = processedSize + batchSize * threads;',
'                 if ( totalChunks == chunked && ajaxCallCounter == totalChunks)',
'                    console.log(''this is the end of ajax'');',
'                 if( chunked == ajaxCallCounter && parser )',
'                    parser.resume();  ',
'                 ',
'            }',
'        } catch { ',
'                    processedSize = totalSize + 1; ',
'                    displayError;',
'                    if ( parser )',
'                        parser.abort();',
'            }',
'',
'        ',
'',
'         ',
'}',
'',
'function Queue() {',
'   this.elements = [];',
'}',
'',
'Queue.prototype.enqueue = function (e) {',
'   this.elements.push(e);',
'};',
'',
'// remove an element from the front of the queue',
'Queue.prototype.dequeue = function () {',
'    return this.elements.shift();',
'};',
'',
'Queue.prototype.isEmpty = function () {',
'    return this.elements.length == 0;',
'};',
'',
'// get the element at the front of the queue',
'Queue.prototype.peek = function () {',
'    return !this.isEmpty() ? this.elements[0] : undefined;',
'};',
'',
'Queue.prototype.length = function() {',
'    return this.elements.length;',
'}',
'',
'//the parsed chunk is enqueue in queue',
'let q = new Queue();',
'',
'',
'function submit(id)',
'	{',
'        submitButtonID = id;',
'		if ($(''#'' + submitButtonID).prop(''disabled'') == "true")',
'			return;',
'        ',
'        bar.animate(0);',
'',
'',
'',
'		stepped = 0;',
'        chunked = 0;',
'		rowCount = 0;',
'		errorCount = 0;',
'		firstError = undefined;',
'',
'		var config = buildConfig();',
'        ',
'		var input = $(''#P2_FILE'').val();',
'',
'',
'',
'		// Allow only one parse at a time',
'		$(''#''+submitButtonID).prop(''disabled'', true);',
'',
'		if (!firstRun)',
'			console.log("--------------------------------------------------");',
'		else',
'			firstRun = false;',
'',
'',
'',
'		if (inputType == "local")',
'		{',
'			if (!$(''#P2_FILE'')[0].files.length)',
'			{',
'				alert("Please choose at least one file to parse.");',
'				return enableButton();',
'			}',
'			',
'            bar.animate(0.1);',
'			$(''#P2_FILE'').parse({',
'				config: config,',
'				before: function(file, inputElem)',
'				{',
'					start = now();',
'					console.log("Parsing file...", file);',
'                    totalChunks = Math.ceil($(''#P2_FILE'')[0].files[0].size/buildConfig().chunkSize);',
'                    ajaxCallCounter = 0;',
'                    bar.animate(0.15);',
'',
'				},',
'				error: function(err, file)',
'				{',
'					console.log("ERROR:", err, file);',
'					firstError = firstError || err;',
'					errorCount++;',
'				},',
'				complete: function()',
'				{',
'                    process(); ',
'                    end = now();',
'					printStats("Done with all files");',
'				}',
'			});',
'		}',
'	}',
'',
'',
'',
'',
'',
'function printStats(msg)',
'{',
'	if (msg)',
'		console.log(msg);',
'	console.log("       Time:", (end-start || "(Unknown; your browser does not support the Performance API)"), "ms");',
'	console.log("  Row count:", rowCount);',
'	if (stepped)',
'		console.log("    Stepped:", stepped);',
'    if (chunked)',
'        console.log("    Chunked:", chunked);    ',
'	console.log("     Errors:", errorCount);',
'	if (errorCount)',
'		console.log("First error:", firstError);',
'}',
'',
'',
'',
'function buildConfig()',
'{',
'	return {',
'		delimiter: '','',',
'		header: false,',
'        chunk: chunkFn,',
'        chunkSize: 1000000,',
'		complete: completeFn,',
'		error: errorFn',
'',
'',
'	};',
'}',
'',
'async function chunkFn(results, parser)',
'{',
'	',
'    chunked++;',
'    bar.animate(bar.value() + 0.01);',
'	if (results)',
'	{',
'		if (results.data) {',
'			rowCount += results.data.length;',
'            if ( q.length() < queueSize ) {',
'                q.enqueue(results.data);',
'            }    ',
'            if ( q.length() == queueSize || q.length() > queueSize ) {',
'                parser.pause();',
'            }    ',
'            let qlen = q.length();     ',
'            while ( !q.isEmpty() && (chunked == totalChunks || qlen == queueSize )) {',
'                ',
'                process(parser);',
'                sleep(300);',
'            }',
'                //parser.resume();',
'            ',
'                    ',
'',
'            //if file is less than chunk size then process once.',
'            if ( buildConfig().chunkSize > $(''#P2_FILE'')[0].files[0].size ) {',
'                process(parser);',
'            }',
'        }    ',
'',
'		if (results.errors)',
'		{',
'			errorCount += results.errors.length;',
'			firstError = firstError || results.errors[0];',
'		}',
'	}',
'}',
'',
'function stepFn(results, parser)',
'{',
'	stepped++;',
'	if (results)',
'	{',
'		if (results.data)',
'			rowCount += results.data.length;',
'		if (results.errors)',
'		{',
'			errorCount += results.errors.length;',
'			firstError = firstError || results.errors[0];',
'		}',
'	}',
'}',
'',
'function completeFn(results)',
'{',
'	end = now();',
'',
'	if (results && results.errors)',
'	{',
'		if (results.errors)',
'		{',
'			errorCount = results.errors.length;',
'			firstError = results.errors[0];',
'		}',
'		if (results.data && results.data.length > 0)',
'			rowCount = results.data.length;',
'	}',
'',
'	printStats("Parse complete");',
'	console.log("    Results:", results);',
'',
'	// icky hack',
'	setTimeout(enableButton, 100);',
'}',
'',
'function errorFn(err, file)',
'{',
'	end = now();',
'	console.log("ERROR:", err, file);',
'	enableButton();',
'}',
'',
'function enableButton()',
'{',
'	$(''#'' + submitButtonID).prop(''disabled'', false);',
'}',
'',
'function now()',
'{',
'	return typeof window.performance !== ''undefined''',
'			? window.performance.now()',
'			: 0;',
'}'))
