'var threads = 1; //number of ajax calls in a batch..ccontrols total max sessions',
'var batchSize = 10; ',
'var totalSize = 100; ',
'var processedSize = 0;',
'var queueSize = 1; //maximum number of parsed chunk of stream array , to trigger ajax call processing',
'var displayError = function(error) {',
'    console.error("Promise is rejected:", error);',
'}',
'var filename;',
'var totalChunks;',
'var bar = new ProgressBar.Circle(progress_container, {',
'        strokeWidth: 6,',
'        easing: ''easeInOut'',',
'        duration: 1400,',
'        color: ''#FFEA82'',',
'        trailColor: ''#eee'',',
'        trailWidth: 1,',
'        svgStyle: null',
'        });',
'var submitButtonID;',
'',
'function insertAfter(referenceNode, newNode) {',
'  referenceNode.insertBefore(newNode, referenceNode.nextSibling);',
'}',
'',
'function getFilename(){',
'    if ($(''#P4_FILE'')[0].files.length) {',
'        return $(''#P4_FILE'')[0].files[0].name;',
'    }',
'    return undefined;',
'			',
'}',
'',
'function sleep(delay) {',
'    var start = new Date().getTime();',
'    while (new Date().getTime() < start + delay);',
'}',
'',
'async function process(parser) {',
'        ',
'        ',
'        var promiseArray = [];',
'        if ( parser )',
'            parser.pause();',
'        ',
'        for (j = 0; j < q.length(); j++) {',
'            var rowArr = q.dequeue();',
'            var sqlArr = [];',
'            var numColumns;',
'            for (k=0;k<rowArr.length;k++) {',
'                var sqlStr = "select " + rowArr[k].map(function(d,idx){return d = "''"+d.replace("''","''''")+"''" + '' as c'' +idx ;}).toString() + " from dual ";',
'                sqlArr.push(sqlStr);',
'                numColumns = rowArr[k].length;',
'            }',
'            var sqlInsertColumnsArr =[];',
'            for (l=1 ; l<=numColumns; l++) {',
'                sqlInsertColumnsArr.push(''c'' + l);',
'            }',
'            var sqlInsertColumns = sqlInsertColumnsArr.join('','');',
'            var sqlInsertStr = "insert into csv_staging2 (" + sqlInsertColumns + ") with csvs as ( " + sqlArr.join ('' union all '') + " )  select * from csvs";   ',
'            ajaxPromise =  apex.server.process(',
'                                "ping", ',
'                                 { p_clob_01: sqlInsertStr},',
'                                {dataType:"text"}',
'                            );                            ',
'            promiseArray.push(ajaxPromise);         ',
'            ',
'        }',
'',
'        try {',
'            ',
'            const resultArray = await Promise.all(promiseArray);',
'            //if ( parser )',
'            //    parser.resume();',
'            for (let i = 0; i<resultArray.length; i++){',
'                 ajaxCallCounter++;',
'                 console.log(resultArray[i] + '' totalChunks: ''+ totalChunks + '' chunked:'' + chunked + '' totalAjax: '' + ajaxCallCounter); ',
'                 bar.animate(ajaxCallCounter/totalChunks);',
'                 processedSize = processedSize + batchSize * threads;',
'                 if ( totalChunks == chunked && ajaxCallCounter == totalChunks)',
'                    console.log(''this is the end of ajax'');',
'                 if( chunked == ajaxCallCounter && parser )',
'                    parser.resume();  ',
'                 ',
'            }',
'        } catch { ',
'                    processedSize = totalSize + 1; ',
'                    displayError;',
'                    if ( parser )',
'                        parser.abort();',
'            }',
'',
'        ',
'',
'         ',
'}',
'',
'function Queue() {',
'   this.elements = [];',
'}',
'',
'Queue.prototype.enqueue = function (e) {',
'   this.elements.push(e);',
'};',
'',
'// remove an element from the front of the queue',
'Queue.prototype.dequeue = function () {',
'    return this.elements.shift();',
'};',
'',
'Queue.prototype.isEmpty = function () {',
'    return this.elements.length == 0;',
'};',
'',
'// get the element at the front of the queue',
'Queue.prototype.peek = function () {',
'    return !this.isEmpty() ? this.elements[0] : undefined;',
'};',
'',
'Queue.prototype.length = function() {',
'    return this.elements.length;',
'}',
'',
'//the parsed chunk is enqueue in queue',
'let q = new Queue();',
'',
'',
'function submit(id)',
'	{',
'        submitButtonID = id;',
'		if ($(''#'' + submitButtonID).prop(''disabled'') == "true")',
