'        ',
'        bar.animate(0);',
'',
'',
'',
'		stepped = 0;',
'        chunked = 0;',
'		rowCount = 0;',
'		errorCount = 0;',
'		firstError = undefined;',
'',
'		var config = buildConfig();',
'        ',
'		var input = $(''#P4_FILE'').val();',
'',
'',
'',
'		// Allow only one parse at a time',
'		$(''#''+submitButtonID).prop(''disabled'', true);',
'',
