'		else',
'			firstRun = false;',
'',
'',
'',
'		if (inputType == "local")',
'		{',
'			if (!$(''#P4_FILE'')[0].files.length)',
'			{',
'				alert("Please choose at least one file to parse.");',
'				return enableButton();',
'			}',
'			',
'            bar.animate(0.1);',
'			$(''#P4_FILE'').parse({',
'				config: config,',
'				before: function(file, inputElem)',
'				{',
'					start = now();',
'					console.log("Parsing file...", file);',
'                    totalChunks = Math.ceil($(''#P4_FILE'')[0].files[0].size/buildConfig().chunkSize);',
